// Extract the token from the response
const token = $input.item.json.access_token;
return { token };

// Extract the array of emails
const emails = $input.item.json.emails;
return { emails };

// Get the emails and proposal files
const emails = $node["Extract Emails"].json.emails;
const proposalFiles = $node["List Proposal PDFs"].json;

// Organize proposal files by type and variant
const proposalsByType = {};

// Package types and their keywords
const PACKAGE_TYPES = {
  "seminar": {
    "keywords": [
      "retreat", "workshop", "seminar", "offsite", "residential",
      "overnight", "accommodation", "stay", "corporate retreat",
      "team building"
    ]
  },
  "meeting": {
    "keywords": [
      "meeting", "conference", "day event", "business meeting",
      "boardroom", "day seminar", "presentation", "training session",
      "client meeting", "executive"
    ]
  },
  "camping": {
    "keywords": [
      "camping", "outdoor", "adventure", "team building", "nature",
      "retreat", "field trip", "expedition", "outdoor activities",
      "student camp", "youth program"
    ]
  },
  "wedding": {
    "keywords": [
      "wedding", "celebration", "ceremony", "reception", "event",
      "party", "banquet", "gala", "anniversary", "special occasion",
      "formal dinner", "celebration"
    ]
  }
};

// Mappings between email templates and the most appropriate package types
const TEMPLATE_TO_PACKAGE = {
  "default": "meeting",          // Default business template -> Meeting package
  "government": "seminar",       // Government agency template -> Seminar package
  "university": "camping",       // University/academic template -> Camping package
  "retreat": "seminar",          // Strategic retreat template -> Seminar package
  "cost": "meeting",             // Cost optimization template -> Meeting package
  "exec_tone": "meeting"         // Executive formal template -> Meeting package
};

// Organization types and their keywords
const ORGANIZATION_TYPES = {
  "corporate": [
    "sdn bhd", "berhad", "limited", "llc", "inc", "corporation", "corp", 
    "pte ltd", "private limited", "enterprise", "company", "consulting",
    "agency", "firm", "partners", "associates", "group"
  ],
  "government": [
    "ministry", "department", "jabatan", "kementerian", "gov", "govt",
    "pejabat", "suruhanjaya", "council", "authority", "board", "commission",
    "public sector", "majlis", "agensi", "negeri", "federal", "pusat"
  ],
  "university": [
    "university", "universiti", "college", "kolej", "institute", "institut",
    "campus", "faculty", "school of", "academic", "education", "higher education",
    "graduate school", "polytechnic", "research center"
  ],
  "school": [
    "school", "sekolah", "academy", "akademi", "primary school", "secondary school",
    "high school", "middle school", "elementary", "kindergarten", "tadika", 
    "preschool", "education center"
  ]
};

// Process proposal files
proposalFiles.forEach(file => {
  const filename = file.name.toLowerCase();

  // Check for seminar packages
  if (filename === "seminar corp package.pdf") {
    if (!proposalsByType['seminar']) proposalsByType['seminar'] = {};
    proposalsByType['seminar']['corporate'] = file.path;
  }
  else if (filename === "seminar gov package.pdf") {
    if (!proposalsByType['seminar']) proposalsByType['seminar']['government'] = file.path;
  }
  // Check for meeting packages
  else if (filename === "meeting corp package.pdf") {
    if (!proposalsByType['meeting']) proposalsByType['meeting'] = {};
    proposalsByType['meeting']['corporate'] = file.path;
  }
  else if (filename === "meeting gov package.pdf") {
    if (!proposalsByType['meeting']) proposalsByType['meeting']['government'] = file.path;
  }
  // Check for camping package
  else if (filename === "camping package.pdf") {
    if (!proposalsByType['camping']) proposalsByType['camping'] = {};
    proposalsByType['camping']['default'] = file.path;
  }
  // Check for wedding package
  else if (filename === "wedding package.pdf") {
    if (!proposalsByType['wedding']) proposalsByType['wedding'] = {};
    proposalsByType['wedding']['default'] = file.path;
  }
});

// Determine organization type from lead data
function determineOrganizationType(lead) {
  const org = (lead.lead_organization || "").toLowerCase();
  const source = (lead.source_url || "").toLowerCase();
  const text = `${org} ${source}`;

  // Check each type in priority order
  for (const orgType in ORGANIZATION_TYPES) {
    for (const keyword of ORGANIZATION_TYPES[orgType]) {
      if (text.includes(keyword.toLowerCase())) {
        return orgType;
      }
    }
  }

  // Default to corporate if no clear match
  return "corporate";
}

// Determine best package type for a lead
function determinePackageType(lead, templateName) {
  // If template is provided, use the mapping
  if (templateName && TEMPLATE_TO_PACKAGE[templateName]) {
    return TEMPLATE_TO_PACKAGE[templateName];
  }

  // Otherwise analyze the lead text
  const org = (lead.lead_organization || "").toLowerCase();
  const role = (lead.lead_role || "").toLowerCase();
  const name = (lead.lead_name || "").toLowerCase();
  const source = (lead.source_url || "").toLowerCase();
  const text = `${org} ${role} ${name} ${source}`;

  // Count keyword matches for each package type
  const matchScores = {};
  for (const pkgType in PACKAGE_TYPES) {
    matchScores[pkgType] = 0;
    for (const keyword of PACKAGE_TYPES[pkgType].keywords) {
      if (text.includes(keyword.toLowerCase())) {
        matchScores[pkgType]++;
      }
    }
  }

  // Find the package with the highest match score
  let bestMatch = null;
  let highestScore = -1;

  for (const pkgType in matchScores) {
    if (matchScores[pkgType] > highestScore) {
      highestScore = matchScores[pkgType];
      bestMatch = pkgType;
    }
  }

  // If no matches at all, use meeting as default
  if (highestScore === 0) {
    return "meeting";
  }

  return bestMatch;
}

// Get appropriate proposal path for a lead
function getProposalPath(lead) {
  const orgType = determineOrganizationType(lead);
  const templateName = lead.template_used || "default";
  const pkgType = determinePackageType(lead, templateName);

  // Check if we have this package type available
  if (!proposalsByType[pkgType]) {
    // Fall back to the first available package type
    const firstAvailableType = Object.keys(proposalsByType)[0];
    if (!firstAvailableType) return null; // No proposals available

    // Try to get the appropriate variant for this org type
    if (proposalsByType[firstAvailableType][orgType]) {
      return proposalsByType[firstAvailableType][orgType];
    }
    // Fall back to default variant
    return proposalsByType[firstAvailableType].default || 
           proposalsByType[firstAvailableType][Object.keys(proposalsByType[firstAvailableType])[0]];
  }

  // Try to get the appropriate variant for this org type
  if (proposalsByType[pkgType][orgType]) {
    return proposalsByType[pkgType][orgType];
  }

  // Fall back to default variant or any available variant for this package type
  return proposalsByType[pkgType].default || 
         proposalsByType[pkgType][Object.keys(proposalsByType[pkgType])[0]];
}

// Match proposals to emails based on lead characteristics
const enrichedEmails = emails.map(email => {
  // Get the appropriate proposal for this lead
  const proposalPath = getProposalPath(email);

  // Add the proposal file path and selection metadata to the email object
  return {
    ...email,
    proposalPath: proposalPath,
    selectedPackageType: determinePackageType(email, email.template_used),
    selectedOrgType: determineOrganizationType(email),
    templateUsed: email.template_used || "default"
  };
});

return { enrichedEmails };

